css:
.body_one{
    height:80%;
    width:100%;
}

.footer{
    position: relative;
    bottom: 0;
    width:100%;
}
.letter-head-footer{
    height:2%;
    justify-content: center;
    align-items: center;
    padding-top:1%;
}
.body_two{
    font-size:12px; 
    color:black; 
    margin-top:20px;
    text-align:left;
    line-height: 1.0;

}
.table1{
	text-align:justify;
	font-size:small;
	width:100%;
    page-break-after:auto
}
th{
    background:#E6E6E6;
    color:black;
    font-size:12px;
}
#col{
    border:1px solid #CBCBCB;
	border-collapse: collapse;
}
#in_word{
    height:100%;
    width:100%;
    color:black;
    padding-top:20px;
}

@media print{
    .footer{
      display: block; page-break-before: none; 
    }
}
