css:
.table2 {
        border-collapse: collapse; /* Ensure table cells share borders */
        width: 100%;
    }

.table2 th, .table2 td {
        border: 1px solid black; /* Add a 1px solid black border to table header and data cells */
        padding: 8px; /* Add padding to the cells for spacing */
        text-align: left; /* Adjust text alignment if needed */
    }
    
tr.noBorder td {
  border: 0;
}

.noBorder{
    border-right: 1px solid black;
    border-left: 1px solid black;
}

tr.lastBorder td {
  border: 0;
}

.lastBorder{
   
    border-bottom:1px solid black;
    border-right: 1px solid black;
    border-left: 1px solid black;
    
}
 .image-container {
     
        display: flex;
        margin-top: 200px;
     
        margin-left:50px;
        justify-content: center;
    }

    .image-container img {
        max-width: 100%; /* Ensure images don't exceed their container */
        height: auto;    /* Maintain image aspect ratio */
    }
