css:
@page {
	size: A4;
	margin-top:0;
	margin-bottom:0;
}

.print-format td, .print-format th {
     padding: 5px !important; 
}
.print-format .letter-head {
    margin-bottom: 0px;
}

#item_table table td, th {
  border: 1px solid #ccc;
  font-size: 11px;
}

#item_table table {
  width: 100%;
  border-collapse: collapse;
}
.page-break {
	page-break-after: always;
}

.header {
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	padding: 10px;
}

.footer {
	position: fixed;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 10px;
}
